(ns examples.hiccup)
(defn user-card [user] (let [admin? (= :admin (:role user)) verified? (:verified user false) age (:age user) classes (if admin? "card admin" "card user")] [:div {:class classes} [:div.header [:h2 (:name user)] (when verified? [:span.badge "âœ“ Verified"])] [:div.body [:p "Age: " age] [:p "Role: " (name (:role user))] (if (>= age 18) [:div.status.adult "Adult account"] [:div.status.minor "Minor account"])] (when admin? [:div.footer [:button {:on-click "deleteUser"} "Delete User"] [:button {:on-click "editPerms"} "Edit Permissions"]])]))
(defn user-list [users] [:div.user-list [:h1 "User Directory"] (if (empty? users) [:p.empty "No users found"] [:div.users (for [u users] [:div {:key (:id u)} (user-card u)])])])
(defn dashboard [data] (let [stats (:stats data) alerts (:alerts data) has-alerts? (seq alerts)] [:div#dashboard [:header [:h1 "Dashboard"] (when has-alerts? [:div.alert-badge (count alerts)])] [:main [:section.stats (for [[k v] stats] [:div.stat {:key k} [:span.label (name k)] [:span.value v]])] (when has-alerts? [:section.alerts [:h2 "Alerts"] (map (fn [alert] [:div.alert {:class (:severity alert)} [:p (:message alert)]]) alerts)])]]))