(ns examples.program20)
(defn form-field [{:keys [id label type value on-change error]}] [:div.form-field [:label {:for id} label] [:input {:value value, :type type, :id id, :class (if error "error" ""), :on-change on-change}] [:span.error-message (or error "")]])
(defn registration-form [] (let [errors {:confirm nil, :email "Invalid email format", :password nil} loading false submitted true] [:form.registration-form [:h2 "Create Account"] (form-field {:value "", :type "email", :label "Email Address", :id "email", :error (:email errors), :on-change (fn [e] nil)}) (form-field {:value "", :type "password", :label "Password", :id "password", :error (:password errors), :on-change (fn [e] nil)}) (form-field {:value "", :type "password", :label "Confirm Password", :id "confirm", :error (:confirm errors), :on-change (fn [e] nil)}) [:div.actions [:button {:disabled loading, :type "submit"} (if loading "Submitting..." "Sign Up")] (when submitted [:div.success-message "Registration successful!"])]]))