(ns examples.program15)
(defn dfs [graph start] (loop [stack [start] visited {} result []] (if (empty? stack) result (let [node (peek stack) new-stack (pop stack)] (if (visited node) (recur new-stack visited result) (let [neighbors (get graph node []) new-visited (conj visited node) new-result (conj result node) new-stack (into new-stack (remove new-visited neighbors))] (recur new-stack new-visited new-result)))))))
(defn bfs [graph start] (loop [queue (conj (clojure.lang.PersistentQueue/EMPTY) start) visited {} result []] (if (empty? queue) result (let [node (peek queue) new-queue (pop queue)] (if (visited node) (recur new-queue visited result) (let [neighbors (get graph node []) new-visited (conj visited node) new-result (conj result node) new-queue (into new-queue (remove new-visited neighbors))] (recur new-queue new-visited new-result)))))))