(ns examples.program20)
(defn form-field [{:keys [id label type error]}] [:div.form-field [:label {:for id} label] [:input {:name id, :type type, :id id, :class (if error "error" "")}] [:span.error-message (or error "")]])
(defn registration-form [] (let [errors {:email "Invalid email", :password nil} loading false submitted true] [:form.registration-form [:h2 "Register"] (form-field {:type "text", :label "Username", :id "username", :error (:username errors)}) (form-field {:type "email", :label "Email", :id "email", :error (:email errors)}) (form-field {:type "password", :label "Password", :id "password", :error (:password errors)}) [:div.actions [:button {:disabled loading, :type "submit"} (if loading "Submitting..." "Submit")] (when submitted [:div.success "Registration successful!"])]]))