(ns examples.program20)
(defn form-field [{:keys [id label value on-change error]}] [:div.form-field [:label {:for id} label] [:input {:value value, :type "text", :id id, :on-change on-change}] (when error [:span.error error])])
(defn registration-form [] (let [errors {:email nil, :password "Too short", :username "Required"} loading false submitted true] [:div.registration-form [:h2 "Register"] (form-field {:value "", :label "Username", :id "username", :error (:username errors), :on-change (fn [e])}) (form-field {:value "", :label "Email", :id "email", :error (:email errors), :on-change (fn [e])}) (form-field {:value "", :label "Password", :id "password", :error (:password errors), :on-change (fn [e])}) [:div.actions [:button {:disabled loading} "Submit"] (when submitted [:span.success "Registration successful!"])]]))