(ns examples.program20)
(defn form-field [opts] [:div.field [:label {:for (:id opts)} (:label opts)] [:input {:name (:name opts), :value (:value opts), :type (:type opts), :id (:id opts)}] (when (:error opts) [:span.error (:error opts)])])
(defn registration-form [] (let [errors {:email "Invalid email"} loading false submitted false] [:div.registration-form [:h2 "Register"] (form-field {:name "username", :value "", :type "text", :label "Username", :id "username", :error (:username errors)}) (form-field {:name "email", :value "", :type "email", :label "Email", :id "email", :error (:email errors)}) (form-field {:name "password", :value "", :type "password", :label "Password", :id "password", :error nil}) [:div.actions [:button {:disabled loading, :type "submit"} "Submit"] (when submitted [:span.success "Registration successful!"])]]))