(ns examples.program20)
(defn form-field [{:keys [id label type value error]}] [:div.form-field [:label {:for id} label] [:input {:value value, :type type, :id id, :class (if error "error" "")}] (when error [:span.error error])])
(defn registration-form [] (let [errors {:email "Invalid email"} loading false submitted false] [:div.registration-form [:h2 "Register"] (form-field {:value "", :type "text", :label "Username", :id "username", :error (:username errors)}) (form-field {:value "", :type "email", :label "Email", :id "email", :error (:email errors)}) (form-field {:value "", :type "password", :label "Password", :id "password", :error (:password errors)}) [:div.actions [:button {:disabled loading, :type "submit"} (if loading "Submitting..." "Register")] (when submitted [:span.success "Registration successful!"])]]))