(ns examples.program15)
(defn dfs [graph start] (loop [stack [start] visited {} result []] (if (empty? stack) result (let [node (peek stack) rest-stack (pop stack)] (if (contains? visited node) (recur rest-stack visited result) (let [new-visited (conj visited node) new-result (conj result node) neighbors (get graph node []) new-stack (into rest-stack neighbors)] (recur new-stack new-visited new-result)))))))
(defn bfs [graph start] (loop [queue [start] visited {} result []] (if (empty? queue) result (let [node (first queue) rest-queue (subvec (vec queue) 1)] (if (contains? visited node) (recur rest-queue visited result) (let [new-visited (conj visited node) new-result (conj result node) neighbors (get graph node []) new-queue (into rest-queue neighbors)] (recur new-queue new-visited new-result)))))))