(ns examples.program15)
(defn dfs [graph start] (loop [stack [start] visited {} result []] (if (empty? stack) result (let [node (peek stack) rest-stack (pop stack)] (if (contains? visited node) (recur rest-stack visited result) (let [neighbors (get graph node []) new-stack (into rest-stack neighbors) new-visited (conj visited node) new-result (conj result node)] (recur new-stack new-visited new-result)))))))
(defn bfs [graph start] (loop [queue (conj (clojure.lang.PersistentQueue/EMPTY) start) visited {} result []] (if (empty? queue) result (let [node (peek queue) rest-queue (pop queue)] (if (contains? visited node) (recur rest-queue visited result) (let [neighbors (get graph node []) new-queue (into rest-queue neighbors) new-visited (conj visited node) new-result (conj result node)] (recur new-queue new-visited new-result)))))))