(ns examples.program20)
(defn form-field [{:keys [id label value on-change error]}] [:div.form-field [:label {:for id} label] [:input {:value value, :type "text", :id id, :on-change on-change}] (when error [:span.error error])])
(defn registration-form [] (let [errors {:username "Required"} loading false submitted false] [:div.registration-form [form-field {:value "", :label "Username", :id "username", :error (:username errors), :on-change (fn [e] nil)}] [form-field {:value "", :label "Email", :id "email", :error (:email errors), :on-change (fn [e] nil)}] [form-field {:value "", :label "Password", :id "password", :error (:password errors), :on-change (fn [e] nil)}] [:div.actions [:button {:disabled loading, :type "submit"} "Register"] (when submitted [:div.success "Registration successful!"])]]))