(ns examples.program14)
(defprotocol Drawable (draw [this]) (bounds [this]))
(defrecord Circle [x y radius] Drawable (draw [this] (str "Drawing circle at " (:x this) ", " (:y this) " with radius " (:radius this))))
(bounds [this] {:max-x (+ (:x this) (:radius this)), :min-y (- (:y this) (:radius this)), :max-y (+ (:y this) (:radius this)), :min-x (- (:x this) (:radius this))})
(defrecord Rectangle [x y width height] Drawable (draw [this] (str "Drawing rectangle at " (:x this) ", " (:y this) " with size " (:width this) "x" (:height this))))
(bounds [this] {:max-x (+ (:x this) (:width this)), :min-y (:y this), :max-y (+ (:y this) (:height this)), :min-x (:x this)})