(ns examples.program20)
(defn form-field [{:keys [id label value on-change error]}] [:div {:class "form-field"} [:label {:for id} label] [:input {:value value, :type "text", :id id, :on-change on-change}] [:span {:class "error"} error]])
(defn registration-form [] (let [errors {:email "Invalid email", :password "Password too short", :username "Username required"} loading false submitted true] [:div {:class "registration-form"} [form-field {:value "", :label "Username", :id "username", :error (:username errors), :on-change (fn [e] nil)}] [form-field {:value "", :label "Email", :id "email", :error (:email errors), :on-change (fn [e] nil)}] [form-field {:value "", :label "Password", :id "password", :error (:password errors), :on-change (fn [e] nil)}] [:div {:class "actions"} [:button {:type "submit"} "Register"] (when submitted [:div {:class "success"} "Registration successful!"])]]))