(ns examples.program20)
(defn form-field [{:keys [id label value on-change error]}] [:div.form-field [:label {:for id} label] [:input {:value value, :type "text", :id id, :on-change on-change}] [:span.error error]])
(defn registration-form [] (let [errors {:email "Invalid email", :password nil} loading false submitted false] [:div.registration-form [:h2 "Register"] [form-field {:value "", :label "Name", :id "name", :error nil, :on-change (fn [e] nil)}] [form-field {:value "", :label "Email", :id "email", :error (:email errors), :on-change (fn [e] nil)}] [form-field {:value "", :label "Password", :id "password", :error (:password errors), :on-change (fn [e] nil)}] [:div.actions [:button {:disabled loading} "Submit"] (when submitted [:div.success "Registration successful!"])]]))