(ns examples.mega-form)
(defn form-field [{:keys [label type value error]}] [:div.field [:label label] [:input {:value value, :type type, :class (when error "error")}] (when error [:span.error-message error])])
(defn registration-form [state] (let [errors (:errors state) loading? (:loading state) submitted? (:submitted state)] [:form.registration {:on-submit (:on-submit state)} [:h2 "Create Account"] (form-field {:value (get-in state [:values :username]), :type "text", :label "Username", :error (:username errors)}) (form-field {:value (get-in state [:values :email]), :type "email", :label "Email", :error (:email errors)}) (form-field {:value (get-in state [:values :password]), :type "password", :label "Password", :error (:password errors)}) [:div.actions [:button {:disabled loading?, :type "submit", :class (if loading? "loading" "")} (if loading? "Creating..." "Create Account")] (when submitted? [:div.success [:p "Account created successfully!"] [:a {:href "/login"} "Go to Login"]])]]))